
public class MathEx02 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// 0 <= x < 1 실수
		System.out.println(Math.random());
		System.out.println(Math.random());
		
		// 0 <= x < 10 실수
		System.out.println( (int)( Math.random()*10) );
		
		// 1 <= x < 46 실수
		System.out.println( (int)( Math.random()*45+1) );
	}

}
