
public class StringEx01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// format 문자열
		// printf 사용
		// String.format
		
		// 다양한 자료 형태 
		// 화면 출력
		System.out.printf( "%s-%s%n", "Hello", "World" );
		
		// 문자열 출력
		String str = String.format("%s-%s", "Hello", "World" );
		System.out.println( str );
	}

}
